DELIMITER $$
CREATE TRIGGER `trg_DATHANG_delete` BEFORE DELETE ON `vp_bill_details` FOR EACH ROW BEGIN
       UPDATE vp_products 
       SET vp_products.prod_qty = vp_products.prod_qty + OLD.quantity 
       WHERE vp_products.prod_id = OLD.product_id; 
    END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `trg_DatHang_insert` BEFORE INSERT ON `vp_bill_details` FOR EACH ROW BEGIN
       UPDATE vp_products 
       SET vp_products.prod_qty = vp_products.prod_qty - new.quantity 
       WHERE vp_products.prod_id = new.product_id; 
    END
$$
DELIMITER ;

